"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[8130],{7735:n=>{n.exports=JSON.parse('{"archive":{"blogPosts":[{"id":"xiaoren","metadata":{"permalink":"/blog/xiaoren","editUrl":"https://github.com/HunDaoLtd/HunDaoLtd.github.io/tree/main/blog/2025-06-19-\u5c0f\u4eba\u754f\u5a01\u4e0d\u754f\u5fb7.md","source":"@site/blog/2025-06-19-\u5c0f\u4eba\u754f\u5a01\u4e0d\u754f\u5fb7.md","title":"\u8d44\u6e90\u56f0\u5c40\u4e2d\u7684\u9053\u5fb7\u6096\u8bba\uff1a\u4e3a\u4ec0\u4e48\u201c\u754f\u5a01\u4e0d\u754f\u5fb7\u201d\u662f\u7406\u6027\u9009\u62e9\uff1f","description":"\u5f88\u591a\u4eba\u53ef\u80fd\u6709\u8fd9\u6837\u7684\u4f53\u4f1a\uff0c\u5728\u8d2b\u7a77\u6216\u6cd5\u6cbb\u7f3a\u5931\u7684\u5730\u65b9\uff0c\u4eba\u4eec\u66f4\u503e\u5411\u4e8e\u670d\u4ece\u5f3a\u6743\u3001\u60e7\u6015\u60e9\u7f5a\uff0c\u800c\u8f7b\u89c6\u751a\u81f3\u8511\u89c6\u9053\u5fb7\u611f\u53ec\u3002\u5728\u4eba\u9645\u4ea4\u5f80\u4e2d\uff0c\u6709\u4e9b\u4eba\u4f1a\u5bf9\u5584\u826f\u5fcd\u8ba9\u8005\u5f97\u5bf8\u8fdb\u5c3a\uff0c\u5374\u5bf9\u6001\u5ea6\u5f3a\u786c\u3001\u6562\u4e8e\u53cd\u51fb\u8005\u6709\u6240\u5fcc\u60ee\u3002\u4ed6\u4eec\u5bf9\u9053\u5fb7\u8c34\u8d23\u53ef\u80fd\u65e0\u52a8\u4e8e\u8877\uff0c\u4f46\u5bf9\u5b9e\u9645\u60e9\u7f5a\uff08\u5982\u6cd5\u5f8b\u540e\u679c\u3001\u5229\u76ca\u635f\u5931\u3001\u6b66\u529b\u5a01\u80c1\uff09\u975e\u5e38\u654f\u611f\u3002","date":"2025-06-19T00:00:00.000Z","tags":[{"inline":true,"label":"Insights","permalink":"/blog/tags/insights"}],"readingTime":4.72,"hasTruncateMarker":true,"authors":[{"name":"\u5b50\u5384","title":"Independent Developer @ Hun","url":"https://github.com/dikpoorcat","page":{"permalink":"/blog/authors/ze"},"socials":{"x":"https://x.com/dikpoorcat","github":"https://github.com/dikpoorcat","zhihu":"https://www.zhihu.com/people/du-ying-cheng"},"imageURL":"https://github.com/dikpoorcat.png","key":"ZE"}],"frontMatter":{"slug":"xiaoren","title":"\u8d44\u6e90\u56f0\u5c40\u4e2d\u7684\u9053\u5fb7\u6096\u8bba\uff1a\u4e3a\u4ec0\u4e48\u201c\u754f\u5a01\u4e0d\u754f\u5fb7\u201d\u662f\u7406\u6027\u9009\u62e9\uff1f","authors":["ZE"],"tags":["Insights"]},"unlisted":false,"nextItem":{"title":"\u4e3a\u4ec0\u4e48\u505a\u8fd9\u4e2a\u94fe\u6e38","permalink":"/blog/why-hun"}},"content":"\u5f88\u591a\u4eba\u53ef\u80fd\u6709\u8fd9\u6837\u7684\u4f53\u4f1a\uff0c\u5728\u8d2b\u7a77\u6216\u6cd5\u6cbb\u7f3a\u5931\u7684\u5730\u65b9\uff0c\u4eba\u4eec\u66f4\u503e\u5411\u4e8e\u670d\u4ece\u5f3a\u6743\u3001\u60e7\u6015\u60e9\u7f5a\uff0c\u800c\u8f7b\u89c6\u751a\u81f3\u8511\u89c6\u9053\u5fb7\u611f\u53ec\u3002\u5728\u4eba\u9645\u4ea4\u5f80\u4e2d\uff0c\u6709\u4e9b\u4eba\u4f1a\u5bf9\u5584\u826f\u5fcd\u8ba9\u8005\u5f97\u5bf8\u8fdb\u5c3a\uff0c\u5374\u5bf9\u6001\u5ea6\u5f3a\u786c\u3001\u6562\u4e8e\u53cd\u51fb\u8005\u6709\u6240\u5fcc\u60ee\u3002\u4ed6\u4eec\u5bf9\u9053\u5fb7\u8c34\u8d23\u53ef\u80fd\u65e0\u52a8\u4e8e\u8877\uff0c\u4f46\u5bf9\u5b9e\u9645\u60e9\u7f5a\uff08\u5982\u6cd5\u5f8b\u540e\u679c\u3001\u5229\u76ca\u635f\u5931\u3001\u6b66\u529b\u5a01\u80c1\uff09\u975e\u5e38\u654f\u611f\u3002\\n\\n\x3c!-- truncate --\x3e\\n\\n\u8fd9\u79cd\u73b0\u8c61\u666e\u904d\u5b58\u5728\uff0c\u5fc5\u6709\u5176\u5185\u5728\u673a\u5236\u3002\u90e8\u5206\u4eba\u7684\u77ed\u89c6\u548c\u8d8b\u5229\u907f\u5bb3\u672c\u80fd\u662f\u539f\u56e0\u4e4b\u4e00\uff0c\u4f46\u8fd9\u4ec5\u9650\u4e8e\u90a3\u4e9b\u66fe\u7ecf\u517b\u6210\u8fd9\u79cd\u884c\u4e3a\u6a21\u5f0f\uff0c\u540e\u6765\u73af\u5883\u53d8\u5316\u5374\u4f9d\u65e7\u4fdd\u7559\u7684\u4eba\u3002\u90a3\u4e48\uff0c\u8fd9\u4e9b\u4eba\u6700\u521d\u4e3a\u4f55\u4f1a\u5f62\u6210\u8fd9\u79cd\u884c\u4e3a\u6a21\u5f0f\u5462\uff1f\\n\\n\u6211\u8ba4\u4e3a\u95ee\u9898\u7684\u6838\u5fc3\u6709\u4e24\u70b9\uff1a\\n1. \u6700\u91cd\u8981\u7684**\u6839\u672c\u8bf1\u56e0**\u662f\u73af\u5883\u8d44\u6e90\u9650\u5236\uff1b\\n2. \u5176\u6b21\u662f\u73af\u5883\u4e2d**\u7fa4\u4f53\u52a8\u6001**\u5904\u4e8e\u201c\u5c0f\u4eba\u591a\u4e8e\u541b\u5b50\u201d\u7684\u72b6\u6001\u3002\\n\\n\u4e3a\u4fbf\u4e8e\u8ba8\u8bba\uff0c\u6211\u4eec\u5c06\u4eba\u7684\u884c\u4e3a\u7b80\u5355\u62bd\u8c61\u4e3a\u201c\u7ade\u4e89\u201d\u4e0e\u201c\u5408\u4f5c\u201d\u4e24\u7c7b\u3002\u628a\u201c\u754f\u5a01\u4e0d\u754f\u5fb7\u201d\u7684\u201c\u5c0f\u4eba\u201d\u884c\u4e3a\u79f0\u4e3a\u300c\u7ade\u4e89\u8005\u300d\u7684\u300c\u7ade\u4e89\u300d\uff0c\u628a\u201c\u754f\u5fb7\u201d\u7684\u201c\u541b\u5b50\u201d\u884c\u4e3a\u79f0\u4e3a\u300c\u5408\u4f5c\u8005\u300d\u7684\u300c\u5408\u4f5c\u300d\u3002\u518d\u4ee5\u4e00\u4e2a\u5ba2\u89c2\u89c4\u5f8b\u4e3a\u524d\u63d0\uff1a\\n- \u5f53\u5408\u4f5c\u8005\u9047\u5230\u5408\u4f5c\u8005\uff0c\u53cc\u65b9\u8d44\u6e90\u90fd\u589e\u52a0\uff08\u53ef\u80fd\u5f88\u5c11\uff0c\u4e5f\u53ef\u80fd\u6210\u500d\u767e\u500d\uff09\uff1b\\n- \u5f53\u5408\u4f5c\u8005\u9047\u5230\u7ade\u4e89\u8005\uff0c\u5408\u4f5c\u8005\u8d44\u6e90\u53d7\u635f\uff0c\u7ade\u4e89\u8005\u8d44\u6e90\u589e\u52a0\uff0c\u4f46\u6574\u4f53\u8d1f\u548c\uff1b\\n- \u5f53\u7ade\u4e89\u8005\u9047\u5230\u7ade\u4e89\u8005\uff0c\u80dc\u8005\u53d7\u635f\u5c0f\u751a\u81f3\u6709\u6536\u76ca\uff0c\u8d25\u8005\u53d7\u635f\u5927\uff0c\u6574\u4f53\u8d1f\u548c\u3002\\n\\n## \u6b63\u5411\u8bba\u8bc1\uff1a\u8d44\u6e90\u532e\u4e4f + \u7ade\u4e89\u8005\u5c45\u591a = \u5408\u4f5c\u7684\u6e83\u8d25\\n\\n\u6211\u4eec\u76f4\u63a5\u4ece\u8fd9\u79cd\u60c5\u51b5\u8ba8\u8bba\uff1a\u5f53\u73af\u5883\u8d44\u6e90\u7a00\u7f3a\uff0c\u4e14\u540c\u7c7b\u4e2d\u7ade\u4e89\u8005\u5360\u6bd4\u9ad8\u3002\u5408\u4f5c\u8005\u66b4\u9732\u5728\u5927\u91cf\u7ade\u4e89\u8005\u9762\u524d\uff0c\u88ab\u63a0\u593a\u6982\u7387\u6781\u9ad8\uff0c\u751f\u5b58\u72b6\u51b5\u8fc5\u901f\u6076\u5316\u3002\u800c\u7ade\u4e89\u8005\uff08\u81f3\u5c11\u77ed\u671f\u5185\uff09\u6d3b\u5f97\u201c\u66f4\u597d\u201d\uff0c\u5408\u4f5c\u8005\u60e8\u6de1\u751a\u81f3\u6d88\u4ea1\u7684\u793a\u8303\u6548\u5e94\uff0c\u4f1a\u8fdb\u4e00\u6b65\u6291\u5236\u5408\u4f5c\u610f\u613f\u3002\u56e0\u6b64\uff0c\u5408\u4f5c\u8005\u9700\u5148\u8bc6\u522b\u540c\u7c7b\uff0c\u4f46\u5728\u9ad8\u6b3a\u8bc8\u73af\u5883\u4e2d\uff0c\u8bc6\u522b\u771f\u6b63\u7684\u5408\u4f5c\u8005\u6210\u672c\u6781\u9ad8\uff08\u9700\u53cd\u590d\u8bd5\u63a2\uff09\uff0c\u5408\u4f5c\u88ab\u5c40\u9650\u4e8e\u201c\u5bb6\u5ead\u201d\u201c\u5708\u5b50\u201d\u7b49\u5c40\u90e8\u8303\u56f4\u3002\u5168\u5c40\u56e0\u6b64\u9677\u5165\u7eb3\u4ec0\u5747\u8861\u7684\u201c\u574f\u201d\u7a33\u5b9a\u6001\uff0c\u4e2a\u4f53\u82e5\u8d38\u7136\u9009\u62e9\u5408\u4f5c\uff0c\u5927\u6982\u7387\u4f1a\u635f\u5931\u8d44\u6e90\u3002\u867d\u7136\u4e5f\u4f1a\u9047\u5230\u5408\u4f5c\u8005\uff0c\u4e14\u5373\u4f7f\u6574\u4f53\u6536\u76ca\uff08\u6307\u7ade\u4e89\u635f\u5931\u4e0e\u5408\u4f5c\u6536\u76ca\u4e4b\u548c\u7684\u6570\u5b66\u671f\u671b\uff09\u4e3a\u6b63\uff0c\u5927\u90e8\u5206\u4eba\u4e5f\u65e0\u6cd5\u9009\u62e9\u5408\u4f5c\u3002\u539f\u56e0\u662f\u81ea\u8eab\u8d44\u6e90\u5b58\u91cf\u4e0d\u8db3\u4ee5**\u591a\u6b21\u8bd5\u9519**\u575a\u6301\u5230\u5408\u4f5c\u5e26\u6765\u5de8\u5927\u56de\u62a5\u3002\u56e0\u4e3a\u5408\u4f5c\u6536\u76ca\u6570\u5b66\u671f\u671b\u867d\u53ef\u80fd\u5f88\u9ad8\uff0c\u4f46\u5206\u914d\u4e25\u91cd\u96c6\u4e2d\u5728\u201c\u7279\u522b\u5de8\u5927\u201d\u7684\u56de\u62a5\uff0c\u5927\u90e8\u5206\u5408\u4f5c\u6536\u76ca\u76f8\u5bf9\u8f83\u4f4e\uff0c\u8fd9\u79cd\u73b0\u8c61\u5728\u5c0f\u4f53\u91cf\u8d44\u6e90\u5408\u4f5c\u4e2d\u6536\u76ca\u7387\u4f4e\uff0c\u5927\u4f53\u91cf\u8d44\u6e90\u5408\u4f5c\u6536\u76ca\u7387\u66f4\u9ad8\uff08\u4e2a\u4eba\u611f\u53d7\u548c\u89c2\u70b9\uff0c\u672a\u5145\u5206\u8bba\u8bc1\uff09\u3002\u6240\u4ee5\uff0c\u5373\u4fbf\u4e2a\u4f53\u6709\u957f\u8fdc\u773c\u5149\uff0c\u77e5\u9053\u5408\u4f5c\u6700\u7ec8\u4f1a\u8d62\uff0c\u4e5f\u65e0\u6cd5\u8f7b\u6613\u9009\u62e9\u5408\u4f5c\uff0c\u56e0\u4e3a\u8fc7\u7a0b\u4e2d\u53ef\u80fd\u5c31\u88ab\u7ade\u4e89\u6dd8\u6c70\u3002\u8fd9\u89e3\u91ca\u4e86\u4e3a\u4f55\u6570\u5b66\u671f\u671b\u4e3a\u6b63\u7684\u5408\u4f5c\u7b56\u7565\u5728\u73b0\u5b9e\u4e2d\u96be\u4ee5\u63a8\u884c\uff1a\u65b9\u5dee\u592a\u5927\u800c\u8d44\u672c\u592a\u8584\u65f6\uff0c\u98ce\u9669\u538c\u6076\u4f1a\u538b\u5012\u7406\u6027\u8ba1\u7b97\u3002\\n\\n## \u53cd\u5411\u8bba\u8bc1\uff1a\u8d44\u6e90\u5145\u8db3 + \u5408\u4f5c\u8005\u5360\u4f18 = \u5408\u4f5c\u7684\u80dc\u5229\\n\\n\u5f53\u8d44\u6e90\u5145\u8db3\u65f6\uff0c\u4e2a\u4f53\u53ef\u4ee5\u653e\u5f03\u90e8\u5206\u77ed\u671f\u7ade\u4e89\u6536\u76ca\uff0c\u8f6c\u800c\u6295\u5165\u5efa\u7acb\u4fe1\u4efb\u548c\u58f0\u8a89\uff0c\u4ee5\u8ffd\u6c42\u5408\u4f5c\u5e26\u6765\u7684\u957f\u671f\u590d\u5229\u3002\u800c\u4e14\uff0c\u4e2a\u4f53\u6709\u8db3\u591f\u7684\u5b89\u5168\u4fdd\u969c\uff0c\u5373\u4fbf\u906d\u9047\u51e0\u6b21\u5408\u4f5c\u5931\u8d25\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u9677\u5165\u56f0\u5883\u3002\u968f\u7740\u5408\u4f5c\u8005\u6bd4\u4f8b\u9010\u6e10\u589e\u52a0\uff0c\u73af\u5883\u751f\u5b58\u538b\u529b\u51cf\u5c0f\uff0c\u201c\u96f6\u548c\u535a\u5f08\u201d\u601d\u7ef4\u6de1\u5316\uff0c\u5408\u4f5c\u5171\u8d62\u7684\u53ef\u80fd\u6027\u663e\u73b0\uff0c\u751a\u81f3\u53ef\u80fd\u5f62\u6210\u5408\u4f5c\u8005\u5360\u4e3b\u5bfc\u7684\u73af\u5883\u3002\\n\\n\u82e5\u4e00\u5f00\u59cb\u5c31\u662f\u5408\u4f5c\u8005\u5360\u5927\u591a\u6570\uff0c\u8fd9\u79cd\u7a33\u5b9a\u72b6\u6001\u4e5f\u80fd\u6301\u7eed\u4e0b\u53bb\uff0c\u7ade\u4e89\u8005\u53cd\u800c\u4f1a\u88ab\u538b\u5236\u5728\u5c11\u6570\uff0c\u751a\u81f3\u96be\u4ee5\u6709\u6240\u4f5c\u4e3a\u3002\u539f\u56e0\u5728\u4e8e\uff0c\u5408\u4f5c\u8005\u8d8a\u591a\uff0c\u627e\u5230\u53ef\u9760\u4f19\u4f34\u7684\u6982\u7387\u8d8a\u5927\uff0c\u5408\u4f5c\u6536\u76ca\u8d8a\u7a33\u5b9a\u3001\u89c4\u6a21\u8d8a\u5927\uff0c\u6210\u529f\u6848\u4f8b\u4f1a\u5f62\u6210\u793a\u8303\u6548\u5e94\u3002\u5728\u5408\u4f5c\u8005\u4e3b\u5bfc\u7684\u793e\u7fa4\u4e2d\uff0c\u9053\u5fb7\u884c\u4e3a\u80fd\u6709\u6548\u6811\u7acb\u58f0\u8a89\u3001\u83b7\u5f97\u652f\u6301\uff0c\u5176\u957f\u671f\u4ef7\u503c\u6e05\u6670\u53ef\u89c1\u4e14\u53ef\u9884\u671f\u3002\u5173\u952e\u662f\uff0c\u7ade\u4e89\u8005\u867d\u6bcf\u6b21\u51fa\u624b\u90fd\u6709\u6536\u76ca\uff0c\u4f46\u8fd9\u4f1a\u6d88\u8017\u81ea\u8eab\u7684\u793e\u4f1a\u8d44\u6e90\uff0c\u4e5f\u5c31\u662f\u4fd7\u8bdd\u8bf4\u7684\u201c\u4e0d\u79ef\u5fb7\u201d\u3002\u5f53\u5fb7\u884c\u5dee\u5230\u4e00\u5b9a\u7a0b\u5ea6\uff0c\u5c31\u4f1a\u9677\u5165\u201c\u5931\u9053\u8005\u5be1\u52a9\u201d\u7684\u5883\u5730\uff0c\u53d7\u5230\u5408\u4f5c\u8005\u7684\u8054\u5408\u6392\u6324\u548c\u5236\u88c1\uff0c\u4e25\u91cd\u65f6\u53ef\u80fd\u89e6\u72af\u5236\u5ea6\u800c\u6d88\u5931\uff0c\u5982\u540c\u73b0\u5b9e\u793e\u4f1a\u4e2d\u89e6\u72af\u6cd5\u5f8b\u53d7\u5211\u4e00\u6837\u3002\u5728\u8fd9\u79cd\u673a\u5236\u4e0b\uff0c\u7ade\u4e89\u8005\u7684\u751f\u5b58\u7a7a\u95f4\u4f1a\u8d8a\u6765\u8d8a\u5c0f\uff0c\u800c\u5408\u4f5c\u8005\u7684\u6536\u76ca\u8d8a\u6765\u8d8a\u5927\uff0c\u6b63\u5e38\u4e2a\u4f53\u90fd\u4f1a\u503e\u5411\u4e8e\u9009\u62e9\u6210\u4e3a\u5408\u4f5c\u8005\uff0c\u6216\u5728\u516c\u5f00\u573a\u5408\u9009\u62e9\u5408\u4f5c\u3002\u56e0\u6b64\uff0c\u5408\u4f5c\u8005\u5360\u4f18\u7684\u73af\u5883\u4f1a\u5f97\u5230\u5f3a\u5316\uff0c\u8fdb\u4e00\u6b65\u5de9\u56fa\u8fd9\u79cd\u7a33\u5b9a\u72b6\u6001\u3002\\n\\n## \u7ed3\u8bed\uff1a\u754f\u5a01\u4e0d\u754f\u5fb7\uff0c\u51fa\u4e8e\u201c\u522b\u65e0\u9009\u62e9\u201d\\n\\n\u7efc\u4e0a\uff0c\u6211\u8ba4\u4e3a**\u73af\u5883\u8d44\u6e90\u538b\u529b**\u548c**\u7fa4\u4f53\u52a8\u6001**\uff08\u7ade\u4e89\u8005\u5360\u591a\u6570\u7684\u73b0\u72b6\uff09\u5171\u540c\u5bfc\u81f4\u4e86\u201d\u5c0f\u4eba\u754f\u5a01\u4e0d\u754f\u5fb7\u201c\u7684\u51fa\u73b0\uff0c\u56e0\u4e3a\u4ed6\u4eec\u522b\u65e0\u9009\u62e9\uff0c\u53ea\u80fd\u7ade\u4e89\uff0c\u65e0\u6cd5\u5408\u4f5c\u3002\\n\\n\u8fd9\u4e2a\u6a21\u578b\u89e3\u91ca\u4e86\u201c\u5c0f\u4eba\u754f\u5a01\u4e0d\u754f\u5fb7\u201d\u5e76\u975e\u5929\u751f\u9053\u5fb7\u8d25\u574f\uff0c\u800c\u662f\u4e25\u9177\u73af\u5883\u4e0e\u7fa4\u4f53\u535a\u5f08\u4e0b\u7684\u9002\u5e94\u6027\u7b56\u7565\u3002\u8981\u6539\u53d8\u8fd9\u79cd\u73b0\u8c61\uff0c\u6839\u672c\u9014\u5f84\u5728\u4e8e\uff1a\u4e00\u662f\u6539\u5584\u57fa\u7840\u751f\u5b58\u6761\u4ef6\uff0c\u7f13\u89e3\u8d44\u6e90\u7ea6\u675f\uff1b\u4e8c\u662f\u5efa\u7acb\u5e76\u7ef4\u62a4\u516c\u5e73\u3001\u53ef\u4fe1\u3001\u80fd\u6709\u6548\u60e9\u7f5a\u80cc\u53db\u3001\u4fdd\u62a4\u5408\u4f5c\u7684\u5236\u5ea6\uff0c\u63d0\u4f9b\u7a33\u5b9a\u7684\u5408\u4f5c\u9884\u671f\u548c\u5236\u88c1\u4e4b\u201c\u5a01\u201d\u3002\\n\\n\u8fd9\u5370\u8bc1\u4e86\u7ba1\u5b50\u6240\u8a00\uff1a\u201c\u4ed3\u5eea\u5b9e\u800c\u77e5\u793c\u8282\uff0c\u8863\u98df\u8db3\u800c\u77e5\u8363\u8fb1\u201d\uff0c\u4e5f\u547c\u5e94\u4e86\u73b0\u4ee3\u5236\u5ea6\u7ecf\u6d4e\u5b66\u7684\u6838\u5fc3\u89c2\u70b9\u2014\u2014\u597d\u7684\u5236\u5ea6\u80fd\u8ba9\u574f\u4eba\u505a\u597d\u4e8b\uff0c\u574f\u7684\u5236\u5ea6\u80fd\u8ba9\u597d\u4eba\u505a\u574f\u4e8b\u3002\\n\\n\\n*\u201c\u672c\u6587\u7075\u611f\u6e90\u4e8e\u4e0eAI\u52a9\u624b\uff08DeepSeek-R1\uff09\u7684\u8ba8\u8bba\uff0c\u7279\u6b64\u8bf4\u660e\u3002\u201d*"},{"id":"why-hun","metadata":{"permalink":"/blog/why-hun","editUrl":"https://github.com/HunDaoLtd/HunDaoLtd.github.io/tree/main/blog/2025-06-13-\u4e3a\u4ec0\u4e48\u505a\u8fd9\u4e2a\u94fe\u6e38.md","source":"@site/blog/2025-06-13-\u4e3a\u4ec0\u4e48\u505a\u8fd9\u4e2a\u94fe\u6e38.md","title":"\u4e3a\u4ec0\u4e48\u505a\u8fd9\u4e2a\u94fe\u6e38","description":"\u603b\u7684\u6765\u8bf4\uff0c\u6211\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u975e\u5e38\u6709\u8da3\u3002","date":"2025-06-13T00:00:00.000Z","tags":[{"inline":false,"label":"Hun","permalink":"/blog/tags/hun","description":"Hun \u662f\u4e00\u6b3e\u533a\u5757\u94fe\u6e38\u620f\uff0c \u4e5f\u662f\u5176\u4e2d NFT \u7684\u540d\u79f0"}],"readingTime":4.2,"hasTruncateMarker":true,"authors":[{"name":"\u5b50\u5384","title":"Independent Developer @ Hun","url":"https://github.com/dikpoorcat","page":{"permalink":"/blog/authors/ze"},"socials":{"x":"https://x.com/dikpoorcat","github":"https://github.com/dikpoorcat","zhihu":"https://www.zhihu.com/people/du-ying-cheng"},"imageURL":"https://github.com/dikpoorcat.png","key":"ZE"}],"frontMatter":{"slug":"why-hun","title":"\u4e3a\u4ec0\u4e48\u505a\u8fd9\u4e2a\u94fe\u6e38","authors":["ZE"],"tags":["hun"]},"unlisted":false,"prevItem":{"title":"\u8d44\u6e90\u56f0\u5c40\u4e2d\u7684\u9053\u5fb7\u6096\u8bba\uff1a\u4e3a\u4ec0\u4e48\u201c\u754f\u5a01\u4e0d\u754f\u5fb7\u201d\u662f\u7406\u6027\u9009\u62e9\uff1f","permalink":"/blog/xiaoren"}},"content":"\u603b\u7684\u6765\u8bf4\uff0c\u6211\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u975e\u5e38\u6709\u8da3\u3002\\n\\n\u4e0d\u4ec5\u56e0\u4e3a\u6211\u60f3\u5236\u4f5c\u4e00\u4e2a\u81ea\u5df1\u559c\u6b22\u7684\u57f9\u80b2\u548c\u517b\u6210\u6e38\u620f\uff0c\u66f4\u56e0\u4e3a\u6211\u80fd\u591f\u6784\u5efa\u4e00\u4e2a\u65b0\u5947\u7684\u3001\u53ea\u6da8\u4e0d\u8dcc\u7684\u7ecf\u6d4e\u7cfb\u7edf\uff0c\u4ee5\u53ca\u4e00\u4e2a\u795e\u5947\u7684\u81ea\u52a8\u7248\u7a0e\u673a\u5236\uff0c\u8fd9\u8ba9\u6211\u611f\u5230\u632f\u594b\u3002\u6211\u5e0c\u671b\u80fd\u591f\u5c1d\u8bd5\u642d\u5efa\u4e00\u4e2a\u771f\u6b63\u5177\u6709\u7ecf\u6d4e\u6b63\u5916\u90e8\u6027\u7684\u4f53\u7cfb\u3002\u6211\u671f\u5f85\u7740\u7406\u60f3\u4e2d\u7684\u94fe\u6e38\u5d1b\u8d77\uff0c\u63a8\u52a8\u4e00\u70b9\u70b9\u8fdb\u6b65\u3002\u6b64\u5916\uff0c\u6211\u4e5f\u60f3\u63a2\u7d22\u94fe\u4e0b\u73a9\u6cd5\uff0c\u8bbe\u8ba1\u4e00\u4e2a\u79bb\u7ebfPVP\u7684\u4f11\u95f2\u6e38\u620f\uff0c\u7ed9\u81ea\u5df1\u8fc7\u8fc7\u763e\u3002\\n\\n\x3c!-- truncate --\x3e\\n\\n### \u7f18\u8d77\\n\\n2020\u5e74\uff0c\u6211\u5f00\u59cb\u73a9 Axie \u94fe\u6e38\uff0c\u4e00\u4e0b\u5b50\u5c31\u559c\u6b22\u4e0a\u4e86\u5b83\u7684\u57f9\u80b2\u73a9\u6cd5\u3002\u76f8\u6bd4\u4e4b\u4e0b\uff0c\u94fe\u4e0b\u90e8\u5206\u7684\u6218\u6597\u6e38\u620f\u5c31\u663e\u5f97\u5f88\u65e0\u8da3\u3002\u5728\u8fd9\u6b3e\u6e38\u620f\u91cc\uff0c\u57f9\u80b2\u51fa\u7279\u5b9a\u6280\u80fd\u7ec4\u5408\u7684\u602a\u7269\u5e76\u4e0d\u5bb9\u6613\uff0c\u4f46\u4e00\u65e6\u5b9e\u73b0\u81ea\u5df1\u7684\u8bbe\u60f3\uff0c\u90a3\u79cd\u6ee1\u8db3\u611f\u96be\u4ee5\u7528\u8a00\u8bed\u5f62\u5bb9\u3002\\n\\n\u540e\u6765\uff0c\u6211\u73a9\u8fc7\u4e00\u6b3e\u505a\u5f97\u5f88\u5dee\u52b2\u7684\u94fe\u6e38\uff0c\u540d\u5b57\u5c31\u4e0d\u8bf4\u4e86\u3002\u5f53\u65f6\u6211\u5fc3\u60f3\uff0c\u8fd9\u4e48\u70c2\u7684\u6e38\u620f\u90fd\u6709\u4eba\u73a9\uff0c\u8fd8\u80fd\u8d5a\u4e0d\u5c11\u94b1\uff0c\u505a\u94fe\u6e38\u4f3c\u4e4e\u633a\u5bb9\u6613\uff0c\u6211\u4e0a\u6211\u4e5f\u884c\u554a\u3002\u4ece\u90a3\u65f6\u8d77\uff0c\u505a\u94fe\u6e38\u7684\u5ff5\u5934\u5f00\u59cb\u840c\u53d1\u3002\\n\\n### \u6a21\u578b\u4e0e\u751f\u6001\\n\\n\u4e4b\u540e\uff0c\u6211\u548c\u670b\u53cb\u4eec\u6709\u4e86\u5f88\u591a\u65b0\u5947\u7684\u60f3\u6cd5\uff0c\u5f00\u59cb\u6709\u4e9b\u8dc3\u8dc3\u6b32\u8bd5\u3002\u5176\u5b9e\uff0c\u6211\u5bf9\u505a\u6e38\u620f\u672c\u8eab\u5174\u8da3\u4e0d\u5927\uff0c\u4f46\u5bf9\u8bbe\u8ba1\u7ecf\u6d4e\u6a21\u578b\u3001\u751f\u6001\u7cfb\u7edf\u548c\u53bb\u4e2d\u5fc3\u5316\u793e\u533a\u5f88\u611f\u5174\u8da3\u3002\u6bd4\u5982\uff0c\u6e38\u620f\u7684\u73a9\u6cd5\u3001\u529f\u80fd\u548c\u6280\u80fd\u8bbe\u8ba1\u7b49\uff0c\u90fd\u53ef\u4ee5\u4ea4\u7ed9\u793e\u533a\u6765\u5b8c\u6210\u548c\u9009\u62e9\u3002\u73a9\u5bb6\u7684\u521b\u610f\u662f\u65e0\u9650\u7684\uff0c\u5927\u5bb6\u80fd\u60f3\u51fa\u5f88\u591a\u6709\u8da3\u7684\u70b9\u5b50\u3002\\n\\n\u8fd9\u6837\uff0c\u6211\u4eec\u53ef\u4ee5\u5171\u540c\u6253\u9020\u5168\u65b0\u7684\u751f\u6001\u7cfb\u7edf\uff0c\u63a2\u7d22\u66f4\u5408\u7406\u3001\u66f4\u826f\u5fc3\u7684\u7ecf\u6d4e\u6a21\u5f0f\uff0c\u800c\u4e0d\u662f\u50cf\u5f88\u591a\u94fe\u6e38\u90a3\u6837\u641e\u5e9e\u6c0f\u9a97\u5c40\u3001\u53ea\u77e5\u9053\u5708\u94b1\uff0c\u751a\u81f3\u7ed9WEB3\u5e26\u6765\u8d1f\u9762\u5f71\u54cd\u3002\u6784\u5efa\u8fd9\u6837\u7684\u7cfb\u7edf\u65e2\u6709\u8da3\u53c8\u6709\u6210\u5c31\u611f\u3002\u800c\u4e14\uff0c\u4f5c\u4e3a\u5e73\u53f0\u65b9\u8fd8\u80fd\u8f7b\u677e\u83b7\u5f97\u62bd\u6210\u6536\u76ca\u3002\\n\\n### \u94fe\u4e0b\u73a9\u6cd5\\n\\n\u53e6\u5916\uff0c\u6211\u60f3\u505a\u8fd9\u4e2a\u94fe\u6e38\uff0c\u8fd8\u56e0\u4e3a\u6211\u559c\u6b22\u79bb\u7ebfPVP\u6e38\u620f\u6a21\u5f0f\u3002\u50cf\u300a\u6d77\u5c9b\u5947\u5175\u300b\uff0c\u4e0d\u7528\u548c\u5176\u4ed6\u73a9\u5bb6\u540c\u65f6\u5728\u7ebf\uff0c\u60f3\u73a9\u7684\u65f6\u5019\u968f\u65f6\u80fd\u73a9\u51e0\u5c40\uff0c\u6bcf\u5c40\u53ea\u9700\u4e09\u4e94\u5206\u949f\uff0c\u5f88\u8f7b\u677e\uff0c\u65e2\u4f11\u95f2\u53c8\u6709\u7b56\u7565\u6027\u3002\u300a\u6d77\u5c9b\u5947\u5175\u300b\u5f00\u6708\u5361\u540e\u53ef\u73a9\u6027\u4e0d\u9519\uff0c\u4f46\u4e0d\u5f00\u6708\u5361\u53ef\u73a9\u6027\u5c31\u5927\u5927\u964d\u4f4e\uff0c\u6700\u7ec8\u7531\u4e8e\u6708\u5361\u592a\u8d35\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u6ca1\u4eba\u966a\u6211\u73a9\u4e86\u3002\\n\\n\u6240\u4ee5\u6211\u60f3\u81ea\u5df1\u505a\u4e00\u6b3e\u7c7b\u4f3c\u7684\u7ebf\u4e0b\u73a9\u6cd5\uff0c\u5982\u679c\u80fd\u6709\u826f\u597d\u7684\u53ef\u73a9\u6027\uff0c\u5438\u5f15\u73a9\u5bb6\u4e00\u8d77\u53c2\u4e0e\u521b\u4f5c\uff0c\u5c31\u80fd\u4e0e\u793e\u533a\u8054\u52a8\u3002\u4f8b\u5982\uff0c\u73a9\u5bb6\u53ef\u4ee5\u5206\u4eab\u81ea\u5df1\u8bbe\u8ba1\u7684\u4f18\u79c0\u5730\u56fe\uff0c\u5927\u5bb6\u70b9\u8d5e\u5f62\u6210\u6392\u884c\u699c\uff1b\u6392\u884c\u699c\u524d\u5217\u7684\u5730\u56fe\u53ef\u4f5c\u4e3aPvE\u7684\u5173\u5361Boss\uff0c\u5730\u56fe\u4f5c\u8005\u4f1a\u5f88\u6709\u6210\u5c31\u611f\uff0c\u56e0\u4e3a\u81ea\u5df1\u7684\u521b\u4f5c\u5f97\u5230\u4e86\u8ba4\u53ef\u5e76\u5e94\u7528\u5230\u6e38\u620f\u4e2d\u3002\u800c\u4e14\uff0c\u8fd9\u79cd\u6a21\u5f0f\u7b56\u7565\u6027\u5f3a\uff0c\u9632\u5b88\u65b9\u548c\u8fdb\u653b\u65b9\u90fd\u662f\u73a9\u5bb6\uff0c\u80fd\u5e26\u6765\u72ec\u7279\u7684\u4e50\u8da3\u3002\u66f4\u91cd\u8981\u7684\u662f\uff0c\u94fe\u4e0b\u6e38\u620f\u53ef\u4ee5\u514d\u8d39\u63d0\u4f9b\u7ed9\u73a9\u5bb6\uff0c\u4e0d\u7528\u518d\u4e3a\u6708\u5361\u7ea0\u7ed3\u3002\u81f3\u4e8e\u8fd0\u8425\u6210\u672c\uff0c\u540e\u9762\u4f1a\u63d0\u5230\u3002\\n\\n\u5982\u679c\u80fd\u6210\u529f\u5efa\u7acb\u751f\u6001\uff0c\u4f9d\u9760\u793e\u533a\u529b\u91cf\uff0c\u57fa\u4e8eNFT\u8fd8\u80fd\u884d\u751f\u51fa\u591a\u79cd\u6e38\u620f\u6a21\u5f0f\u3002\u6211\u60f3\u505a\u7684\u94fe\u4e0b\u73a9\u6cd5\u53ea\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u5176\u4ed6\u5f00\u53d1\u8005\u82e5\u6709\u521b\u610f\uff0c\u53ef\u5229\u7528\u94fe\u6e38\u7684NFT\u63a5\u5165\u7cfb\u7edf\uff0c\u81ea\u7531\u8bbe\u8ba1\u548c\u5f00\u53d1\u5404\u79cd\u73a9\u6cd5\u3002\u5982\u6b64\u4e00\u6765\uff0c\u8fd9\u4e2a\u751f\u6001\u5efa\u7acb\u540e\u5c06\u662f\u6240\u6709\u4eba\u5171\u4eab\u7684\u3002\u6570\u5b57\u4e16\u754c\u5c31\u8be5\u66f4\u81ea\u7531\u3002\\n\\n### \u7248\u7a0e\u673a\u5236\\n\\n\u94fe\u6e38\u6a21\u5f0f\u6709\u4e2a\u663e\u8457\u4f18\u52bf\uff0c\u5b83\u4e0d\u50cf\u4f20\u7edf\u6e38\u620f\u90a3\u6837\uff0c\u4e3a\u4e86\u8fd0\u8425\u548c\u76c8\u5229\u8bbe\u7f6e\u5927\u91cf\u6536\u8d39\u70b9\uff0c\u5982\u6708\u5361\u6216\u7834\u574f\u6e38\u620f\u5e73\u8861\u7684\u9053\u5177\u552e\u5356\u3002\u94fe\u6e38\u6a21\u5f0f\u751f\u5b58\u538b\u529b\u8f83\u5c0f\uff0c\u53ef\u901a\u8fc7\u53d1\u5e01\u7b49\u65b9\u5f0f\u878d\u8d44\u3002\u4e0d\u8fc7\uff0c\u6211\u8bbe\u8ba1\u7684\u7ecf\u6d4e\u6a21\u578b\u65e0\u6cd5\u53d1\u5e01\u878d\u8d44\uff0c\u53ea\u80fd\u5148\u4e3a\u7231\u53d1\u7535\uff0c\u7b49\u751f\u6001\u53d1\u5c55\u8d77\u6765\u540e\uff0c\u901a\u8fc7\u7248\u7a0e\u8986\u76d6\u5f00\u53d1\u548c\u8fd0\u8425\u6210\u672c\uff0c\u5b9e\u73b0\u81ea\u7ed9\u81ea\u8db3\u3002\\n\\n\u73b0\u6709\u7684\u7248\u7a0e\u673a\u5236\u4e00\u822c\u57fa\u4e8e\u4ea4\u6613\u62bd\u6210\u3002\u6bd4\u5982A\u628a\u4f5c\u54c1\u5356\u7ed9B\uff0c\u62bd3\u6210\uff1bB\u518d\u5356\u7ed9C\uff0c\u53c8\u62bd3\u6210\u3002\u6bcf\u6b21\u4ea4\u6613\u989d\u90fd\u8981\u7ed9\u4f5c\u8005\u62bd\u6210\uff0c\u6211\u89c9\u5f97\u8fd9\u4e0d\u5408\u7406\u3002\u56e0\u4e3a\u6211\u5356\u6389\u4f5c\u54c1\u53ef\u80fd\u8fd8\u4e8f\u672c\uff0c\u4f46\u6210\u672c\u90e8\u5206\u4e5f\u88ab\u62bd\u6210\u4e86\u3002\\n\\n\u6b64\u5916\uff0c\u73b0\u6709 NFT \u4eba\u4e3a\u5236\u9020\u7a00\u7f3a\u6027\uff0c\u5728\u6570\u5b57\u4e16\u754c\u660e\u660e\u80fd\u65e0\u9650\u590d\u5236\u7c98\u8d34\uff0c\u5374\u504f\u8981\u6a21\u4eff\u73b0\u5b9e\u4e16\u754c\u3002\u6240\u4ee5\u6211\u8bbe\u8ba1\u7684\u7ecf\u6d4e\u6a21\u578b\u6d88\u9664\u4e86\u4eba\u4e3a\u7a00\u7f3a\u6027\uff0c\u4f5c\u54c1\u53ef\u65e0\u9650\u590d\u5236\uff0c\u4efb\u4f55\u4eba\u90fd\u80fd\u83b7\u53d6\u3002\u4e0d\u8fc7\u7a00\u7f3a\u6027\u4f9d\u7136\u5b58\u5728\uff0c\u4f53\u73b0\u5728\u7248\u7a0e\u6700\u7ec8\u4f1a\u56de\u9988\u7ed9\u4f5c\u8005\u3002\u8fd9\u79cd\u7a00\u7f3a\u6027\u5728\u4e8e\u53ea\u6709\u4f5c\u8005\u80fd\u83b7\u5f97\u6536\u76ca\uff0c\u800c\u4f5c\u54c1\u672c\u8eab\u53ef\u88ab\u65e0\u6570\u4eba\u62e5\u6709\u3002\u6bcf\u6b21\u590d\u5236\u4f5c\u54c1\u65f6\uff0c\u4f1a\u5c06\u590d\u5236\u6240\u5f97\u7684\u4e00\u90e8\u5206\u6536\u76ca\u76f4\u63a5\u652f\u4ed8\u7ed9\u4f5c\u8005\u3002\\n\\n\u6211\u8ba4\u4e3a\u8fd9\u79cd\u6a21\u5f0f\u7b26\u5408\u53d1\u5c55\u89c4\u5f8b\uff0c\u672a\u6765\u5f88\u53ef\u80fd\u6210\u4e3a\u4e3b\u6d41\uff0c\u65e0\u8bba\u662f\u6587\u7ae0\u3001\u97f3\u4e50\u8fd8\u662f\u753b\u4f5c\uff0c\u6216\u8bb8\u90fd\u4f1a\u91c7\u7528\u8fd9\u79cd\u6a21\u5f0f\u3002\u6bd5\u7adf\u8eba\u7740\u8d5a\u94b1\u8c01\u4e0d\u559c\u6b22\uff1f\u4eba\u5728\u5bb6\u4e2d\u7761\uff0c\u7248\u7a0e\u4ece\u5929\u4e0a\u6765\u7684\u68a6\u60f3\uff0c\u4ece Hun \u5f00\u59cb\uff0c\u4eba\u4eba\u90fd\u80fd\u5b9e\u73b0\uff01"}]}}')}}]);